mapper.insert:
insert into academic_calendar(
	  aca_no
	, aca_sdate
	, aca_edate
	, aca_title
	, aca_content			
	, aca_color
)
values(
	  'ACA_'||lpad(seq_academic_calendar.nextval, 4, '0') 
	, #{acaSdate}
	, #{acaEdate}
	, #{acaTitle}
	, #{acaContent}			
	, #{acaColor}
)

mapper.update:
update academic_calendar
   set
       aca_sdate = #{acaSdate}
     , aca_edate = #{acaEdate}
     , aca_title = #{acaTitle}
     , aca_content = #{acaContent}
     , aca_color = #{acaColor}
 where aca_no = #{acaNo}

update year_semester
   set 
       ys_sdate = #{ysSdate}
     , ys_edate = #{ysEdate}
 where ys_year = #{ysYear}
   and ys_semester = #{ysSemester}
